SELECT cj.[ConciergeCaseID],
       cj.[CaseID],
       cj.[OneClickAccountID],
       cj.[AgencyID],
       pa.AgencyName,
       pa.ParentAgencyName,
       cj.[CarrierName],
       cj.[CaseType],
       cj.[CaseStatusCategory],
       cj.[CaseStatus],
       cj.[CaseStatusDetail],
       cj.[AnnualPremium],
       cj.[FulfillmentCreatedDate] [App_Request_Date],
       cj.[LifeCaseCreatedDate] [App_Submit_Date],
       cj.[UpdatedDate],
       cj.[CasePaidDate] [App_Paid_Date],
       cj.[IsDirectToConsumer],
       cj.[COVR_User],
       cj.[COVR_UserId],
       cj.[ProductCategory],
       cj.[PlanType],
       cj.[PlanName],
       cj.[ClientFirstName],
       cj.[ClientLastName],
       cj.[ClientEmail],
       cj.[ClientPhone],
       cj.[ClientAddress],
       cj.[ClientCity],
       cj.[ClientState],
       cj.[ClientZip],
       cj.[ClientBirthDate],
       cj.[FaceAmount],
       cj.[QuotedTerm],
       cj.[QuotedHealthClass],
       cj.[QuotedProduct],
       cj.[QuotedCarrier],
       cj.[CaseSource],
       cj.[DataField1Name],
       cj.[DataField1Value],
	   '' AS [DataField2Name],
	   '' AS [DataField2Value],
	   '' AS [DataField3Name],
	   '' AS [DataField3Value],
       cj.[PartnerReferralID],
cj.isDigital
FROM [powerbi].[covr_journey] cj
    INNER JOIN dbo.ParentAgency pa
        ON pa.AgencyId = cj.AgencyID
		
ORDER BY cj.FulfillmentCreatedDate DESC,
         cj.LifeCaseCreatedDate DESC;


